import { push } from 'connected-react-router';
import { db, FirebaseTimestamp } from '../../firebase';
import { deleteMenuAction, fetchMenusAction } from './actions';
import firebase from 'firebase';


const menusRef = db.collection('restaurants');

export const deleteMenu = (id) => {
  return async(dispatch, getState) => {
    menusRef.doc(id).delete()
      .then(() => {
        const prevMenus = getState().menus.list
        const nextMenus = prevMenus.filter(menu => menu.id !== id)
        dispatch(deleteMenuAction(nextMenus))
      })
  }
}

export const fetchMenus = () => {
  return async(dispatch) => {
    menusRef.get()
      .then(snapshots => {
        const menuList = []
        snapshots.forEach(snapshot => {
          const menu = snapshot.data()
          menuList.push(menu)
        })
        dispatch(fetchMenusAction(menuList))
      })
  }
}

export const saveMenu = (restId, id, name, price, star, images) => {
  return async(dispatch) => {
    const timestamp = FirebaseTimestamp.now()
    let tmp = 0;

    if (id === "") {
      const S="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
      const N=16;
      tmp = Array.from(crypto.getRandomValues(new Uint32Array(N))).map((n)=>S[n%S.length]).join('')
    } else {
      tmp = id;
    }

    const data = {
      id: tmp,
      name: name,
      price: parseInt(price, 10),
      star: star,
      images: images,
      updated_at: timestamp
    }

    return menusRef.doc(restId).update({menus: firebase.firestore.FieldValue.arrayUnion(data)})
      .then(() => {
        dispatch(push('/restaurant/'+ restId + '/menu/'))
      }).catch((error) => {
        throw new Error(error)
      })
  }
}
