import { makeStyles } from '@material-ui/core/styles';
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { db } from '../firebase';

const useStyles = makeStyles((theme) => ({
  sliderBox: {
    [theme.breakpoints.down('sm')]: {
      margin: '0 auto 24px auto',
      height: 320,
      width: 320
    },
    [theme.breakpoints.up('sm')]: {
      margin: '0 auto',
      height: 400,
      width: 400
    },
  },
  detail: {
    textAlign: 'left',
    [theme.breakpoints.down('sm')]: {
      margin: '0 auto 16px auto',
      height: 'auto',
      width: 320
    },
    [theme.breakpoints.up('sm')]: {
      margin: '0 auto',
      height: 'auto',
      width: '400'
    }
  },
  price: {
    fontSize: 36
  }
}))

const MenuDetail = () => {
  const classes = useStyles();
  const selector = useSelector((state) => state);
  const path = selector.router.location.pathname;
  const id = path.split('/menu/')[1];ã€€

  const [menu, setMenu] = useState(null);

  useEffect(() => {
    db.collection('menus').doc(id).get()
      .then(doc => {
        const data = doc.data();
        setMenu(data)
      })
  }, [])

  return (
    <section className='c-section-wapin'>
      {menu && (
        <div className='p-grid__row'>
          <div className={classes.sliderBox}></div>
          <div className={classes.detail}>
            <h2 className='u-text__headline'>{menu.menu}</h2>
            <p className={classes.price}>{menu.price.toLocaleString()}</p>
            <div className='module-spacer--small' />
            <div className='module-spacer--small' />
          </div>
        </div>
      )}
    </section>
  )
}

export default MenuDetail
