import { makeStyles } from '@material-ui/core/styles';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { db } from '../firebase';
import { ImageSwiper } from '../components/Uikit';
import { push } from 'connected-react-router';
import { getMenusId } from '../reducks/menus/selectors';
import { getRestaurantId } from '../reducks/restaurants/selectors';

const useStyles = makeStyles((theme) => ({
  sliderBox: {
    [theme.breakpoints.down('sm')]: {
      margin: '0 auto 24px auto',
      height: 320,
      width: 320
    },
    [theme.breakpoints.up('sm')]: {
      margin: '0 auto',
      height: 400,
      width: 400
    },
  },
  detail: {
    textAlign: 'left',
    [theme.breakpoints.down('sm')]: {
      margin: '0 auto 16px auto',
      height: 'auto',
      width: 320
    },
    [theme.breakpoints.up('sm')]: {
      margin: '0 auto',
      height: 'auto',
      width: '400'
    }
  },
  price: {
    fontSize: 36
  },
  star: {
    fontSize: 36,
    color: 'red'
  }
}))

const MenuDetail = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const selector = useSelector((state) => state);
  const restId = getRestaurantId(selector)
  const id = getMenusId(selector);

  const [menu, setMenu] = useState(null);

  const stars = {
    1:'★',
    2:'★★',
    3:'★★★',
    4:'★★★★',
    5:'★★★★★'
  }

  useEffect(() => {
    db.collection('restaurants').doc(restId).collection('menus').doc(id).get()
      .then(doc => {
        const data = doc.data();
        setMenu(data)
      })
  }, [])

  return (
    <section className='c-section-wapin'>
      <div className='module-spacer--small' />
      <div className='module-spacer--small' />
      {menu && (
        <div className='p-grid__row'>
          <div className={classes.sliderBox}>
            <ImageSwiper images={menu.images} />
          </div>
          <div className={classes.detail}>
            <h2 className='u-text__headline'>{menu.menu}</h2>
            <p className={classes.price}>{menu.price.toLocaleString()}</p>
            <p className={classes.star}>{stars[menu.star]}</p>
            <div className='module-spacer--small' />
            <div className='module-spacer--small' />
            <p onClick={() => {dispatch(push('/restaurant/'+ restId + '/menu/'))}}>一覧へ戻る</p>
          </div>
        </div>
      )}
    </section>
  )
}

export default MenuDetail
