import { push } from 'connected-react-router';
import { db, FirebaseTimestamp } from '../../firebase';

const menusRef = db.collection('menus');

export const fetchMenus = () => {
  return async(dispatch) => {
    menusRef.orderBy('updated_at', 'desc')
  }
}

export const saveMenue = (id, menu, price, star, images) => {
  return async(dispatch) => {
    const timestamp = FirebaseTimestamp.now()

    const data = {
      menu: menu,
      price: parseInt(price, 10),
      star: star,
      images: images,
      updated_at: timestamp
    }
    
    if (id === "") {
      const ref = menusRef.doc()
      const id = ref.id;
      data.id = id
      data.create_at = timestamp
    }

    return menusRef.doc(id).set(data, {merge: true})
      .then(() => {
        dispatch(push('/'))
      }).catch((error) => {
        throw new Error(error)
      })
  }
}
