import { push } from "core-js/core/array";
import { signInAction } from "./actions";

export const signIn = () => {
  return async(dispatch, getState) => {
    const state = getState()
    const isSigneIn = state.users.isSigneIn

    if(!isSigneIn) {
      const username = "Choushi Daiki"
      const uid = "71480293"

      dispatch(signInAction({
        isSigneIn: true,
        uid: uid,
        username: username
      }))
      dispatch(push('/'))
    }
  }
}
