import { push } from 'connected-react-router';
import { db, FirebaseTimestamp } from '../../firebase';
import { deleteMenuAction, fetchMenusAction } from './actions';


// const menusRef = db.collection('restarurants');
const menusRef = db.collection('menus');

export const deleteMenu = (id) => {
  return async(dispatch, getState) => {
    menusRef.doc(id).delete()
      .then(() => {
        const prevMenus = getState().menus.list
        const nextMenus = prevMenus.filter(menu => menu.id !== id)
        dispatch(deleteMenuAction(nextMenus))
      })
  }
}

export const fetchMenus = () => {
  return async(dispatch) => {
    menusRef.orderBy('star', 'desc').get()
      .then(snapshots => {
        const menuList = []
        snapshots.forEach(snapshot => {
          const menu = snapshot.data()
          menuList.push(menu)
        })
        dispatch(fetchMenusAction(menuList))
      })
  }
}

export const saveMenu = (restId, id, menu, price, star, images) => {
  return async(dispatch) => {
    const timestamp = FirebaseTimestamp.now()

    // const data = {
    //   menu: menu,
    //   price: parseInt(price, 10),
    //   star: star,
    //   images: images,
    //   updated_at: timestamp
    // }

    const data = {
      menus:{
        name: menu,
        price: parseInt(price, 10),
        star: star,
        images: images,
        updated_at: timestamp
      }
    }
    
    // if (id === "") {
    //   const ref = menusRef.doc()
    //   data.created_at = timestamp;
    //   id = ref.id;
    //   data.id = id;
    // }


    return menusRef.doc().set(data, {merge: true})
      .then(() => {
        dispatch(push('/restaurant/'+ restId + '/menu/'))
      }).catch((error) => {
        throw new Error(error)
      })
  }
}
