import { createSelector } from "reselect";

const restaurantsSelector = (state) => state.restaurants;
const idSelector = (state) => state.router.location.pathname.split('/menu/')[1];

const makeId = (state) => {
  const id = state.router.location.pathname.split('/menu/')[1];
  if(id.split('/menu')) {
    return id.split('/menu')[0];
  } else {
    return id;
  }
}

export const getRestaurant = createSelector(
  [restaurantsSelector],
  state => state.list
)

export const getRestaurantId = createSelector(
  [idSelector],
  state => state,
)
