import { push } from 'connected-react-router';
import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch } from 'react-redux';
import { ImageArea } from '../components/Uikit';
import { PrimaryButton, SelectBox, TextInput } from '../components/Uikit';
import { saveMenue } from '../reducks/menus/operations';
import { db } from '../firebase/index';

const RestaurantEdit = () => {
  const dispatch = useDispatch();
  let id = window.location.pathname.split('/restaurant/edit')[1];

  if(id !== "") {
    id = id.split('/')[1]
  };

  const [name, setName] = useState(""),
        [pref, setPref] = useState(""),
        [images, setImages] = useState([]),
        [genre, setGenre] = useState("");

  const inputName = useCallback((event) => {
    setMenu(event.target.value)
  }, [setName]);

  const inputPrice = useCallback((event) => {
    setPref(event.target.value)
  }, [setPref]);

  useEffect(() => {
    if(id !== "") {
      db.collection('restaurants').doc(id).get()
        .then(snapshot => {
          const data = snapshot.data();
          setMenu(data.name);
          setPrice(data.pref);
          setImages(data.images);
          setStar(data.genre);
        })
    }
  }, [id])

  return (
    <section>
      <h2 className='u-text__headline u-text-center'>投稿・編集</h2>
      <div className='c-section-container'>
        <ImageArea images={images} setImages={setImages} />
        <TextInput
          fullWidth={true} label={"メニュー名"} multiline={false} required={true}
          onChange={inputMenu} rows={1} value={menu} type={"text"}
        />
        <TextInput
          fullWidth={true} label={"価格"} multiline={false} required={true}
          onChange={inputPrice} rows={1} value={price} type={"number"}
        />
        <SelectBox
          label={"評価"} required={true} options={stars} select={setStar} value={star}
        />
        <div className='module-spacer--medium' />
        <div className="center">
          <PrimaryButton
            label={"保存"}
            onClick={() => dispatch(saveMenue(id, menu, price, star, images))}
          />
        </div>
        <div className='module-spacer--medium' />
        <p className="center" onClick={() => dispatch(push('/'))}>ホームに戻る</p>
      </div>
    </section>
  )
}

export default RestaurantEdit
