import React, { useCallback, useState } from 'react'
import { TextInput } from '../components/Uikit';

const MenuEdit = () => {

  const [menu, setMenu] = useState(""),
        [price, setPrice] = useState(""),
        [star, setStar] = useState("");

  const inputMenu = useCallback((event) => {
    setMenu(event.target.value)
  }, [setMenu]);

  const inputPrice = useCallback((event) => {
    setPrice(event.target.value)
  }, [setPrice]);

  return (
    <section>
      <h2 className='u-text__headline u-text-center'>投稿・編集</h2>
      <div className='c-section-container'>
        <TextInput
          fullWith={true} label={"メニュー名"} multiline={false} required={true}
          onChange={inputMenu} rows={1} value={menu} type={"text"}
        />
        <TextInput
          fullWith={true} label={"価格"} multiline={false} required={true}
          onChange={inputPrice} rows={1} value={price} type={"number"}
        />
      </div>
    </section>
  )
}

export default MenuEdit
