import { push } from 'connected-react-router'
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RestaurantCard } from '../components/Restaurants'
import { PrimaryButton } from '../components/Uikit'
import { fetchMenus } from '../reducks/menus/operations'
import { getMenus } from '../reducks/menus/selectors'

const RestaurantList = () => {
  const dispatch = useDispatch();
  const selector = useSelector((state) => state);
  const menus = getMenus(selector);

  useEffect(() => {
    dispatch(fetchMenus())
  }, []);

  return (
    <div>
      <section className='c-section-wrapin'>
        <div className='p-grid__row'>
          {menus.length > 0 && (
            menus.map(menu => (
              <MenuCard
                key={menu.id} id={menu.id} menu={menu.menu}
                images={menu.images} price={menu.price} star={stars[menu.star]}
              />
            ))
          )}
        </div>
        <div className='module-spacer--small' />
        <div>
          <PrimaryButton
            label={"店舗を追加"}
            onClick={() => {dispatch(push('/restaurant/edit'))}}
          />
          <div className='module-spacer--small' />
          <PrimaryButton
            label={"メニューを追加"}
            onClick={() => {dispatch(push('/menu/edit'))}}
          />
        </div>
      </section>
    </div>
  )
}

export default RestaurantList
