import { push } from 'connected-react-router';
import React, { useCallback, useState } from 'react'
import { useDispatch } from 'react-redux';
import ImageArea from '../components/Menus/ImageArea';
import { PrimaryButton, SelectBox, TextInput } from '../components/Uikit';
import { saveMenue } from '../reducks/menus/operations';

const MenuEdit = () => {
  const dispatch = useDispatch();

  const [menu, setMenu] = useState(""),
        [price, setPrice] = useState(""),
        [star, setStar] = useState("");

  const inputMenu = useCallback((event) => {
    setMenu(event.target.value)
  }, [setMenu]);

  const inputPrice = useCallback((event) => {
    setPrice(event.target.value)
  }, [setPrice]);

  const stars = [
    {id: "1", name: "★"},
    {id: "2", name: "★★"},
    {id: "3", name: "★★★"},
    {id: "4", name: "★★★★"},
    {id: "5", name: "★★★★★"}
  ]

  return (
    <section>
      <h2 className='u-text__headline u-text-center'>投稿・編集</h2>
      <div className='c-section-container'>
        <ImageArea />
        <TextInput
          fullWidth={true} label={"メニュー名"} multiline={false} required={true}
          onChange={inputMenu} rows={1} value={menu} type={"text"}
        />
        <TextInput
          fullWidth={true} label={"価格"} multiline={false} required={true}
          onChange={inputPrice} rows={1} value={price} type={"number"}
        />
        <SelectBox
          label={"評価"} required={true} options={stars} select={setStar} value={star}
        />
        <div className='module-spacer--medium' />
        <div className="center">
          <PrimaryButton
            label={"保存"}
            onClick={() => dispatch(saveMenue(menu, price, star))}
          />
        </div>
        <div className='module-spacer--medium' />
        <p className="center" onClick={() => dispatch(push('/'))}>ホームに戻る</p>
      </div>
    </section>
  )
}

export default MenuEdit
